import { useState, useCallback, useRef, useEffect} from "react";
import {
    APIProvider,
    Map as GoogleMap,
    Marker,
} from '@vis.gl/react-google-maps';

function Map({markers, setMarkers}) {
    const mapRef = useRef(null);

    const onMapClick = useCallback(async (e) => {
        const latLng = e.detail.latLng;
        const res = await fetch(process.env.REACT_APP_SERVER_API_URL + "/pin", {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(latLng),
        });

        if (!res.ok) {
            setMarkers((prevMarkers) => {
                return [...prevMarkers, latLng];
            });
        }
    }, []);


    return (
        <APIProvider apiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
            <GoogleMap
                ref={mapRef}
                style={{width: '100%',height: '500px',}}
                defaultCenter={{lat: 50.06255275511061, lng: 19.939309651193764}}
                defaultZoom={14}
                onClick={onMapClick}
            >
            {markers.map((marker, idx) => (
                <Marker key={idx} position={marker} />
            ))}
            </GoogleMap>
        </APIProvider>
    );
}

export default Map;
