import React from 'react';
import {useEffect, useState} from 'react';
import dotenv from 'dotenv';

import './LocationEntry';


function LocationList() {
    const [locations, setLocations] = useState([]);
    const [page, setPage] = useState(1);

    useEffect(() => {
        const locations = fetch(dotenv.SERVER_API_URL + `/locations?page=${page}&limit=5`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
            .catch(error => console.error('Error fetching locations:', error));
    }, [page]);

    return (
        <div className="location-list">
            {locations.map(location => (
                <LocationEntry key={location.id} location={location} />
            ))}

            <div className="pagination-controls">
                <button onClick={() => setPage((page) => page - 1)} disabled={page === 1}>
                    Poprzednia
                </button>
                <button onClick={() => setPage((page) => page + 1)} disabled={locations.length < 1}>
                    NastÄ™pna
                </button>
            </div>
        </div>
    );
}

export default LocationList;
