import {useEffect, useState} from 'react';

import LocationEntry from './LocationEntry';


function LocationList({pins, setPins}) {
    const page_size = 5;

    const [locations, setLocations] = useState(pins.slice(0, page_size));
    const [page, setPage] = useState(1);

    function nextPage() {
        setPage(page + 1);
        setLocations(pins.slice((page - 1) * page_size, page * page_size));
    }

    function prevPage() {
        setPage(page - 1);
        setLocations(pins.slice((page - 2) * page_size, (page - 1) * page_size));
    }


    return (
        <div className="location-list">
            <p text={locations} />
            {locations.map(location => (
                <LocationEntry key={location.id} location={location} />
            ))}

            <div className="pagination-controls">
                <button onClick={prevPage} disabled={page === 1}>
                    Poprzednia
                </button>
                <button onClick={nextPage} disabled={page * page_size >= pins.length}>
                    NastÄ™pna
                </button>
            </div>
        </div>
    );
}

export default LocationList;
