import { useState } from 'react';

function LocationEntry({ pin, onEdit, onDelete }) {
    const [isEditing, setIsEditing] = useState(false);
    const [newName, setNewName] = useState(pin.name);

    const handleEdit = () => {
        if (isEditing) {
            onEdit(pin.id, newName); // Wywołanie funkcji edycji
        }
        setIsEditing(!isEditing);
    };

    const handleDelete = () => {
        onDelete(pin.id); // Wywołanie funkcji usuwania
    };

    return (
        <div className="location-entry">
            {isEditing ? (
                <input
                    type="text"
                    value={newName}
                    onChange={(e) => setNewName(e.target.value)}
                />
            ) : (
                <h2>{pin.name}</h2>
            )}
            <p>Coordinates: {pin.loc.coordinates[1]}, {pin.loc.coordinates[0]}</p>
            <p>Creation date: {pin.creationTime}</p>
            <button onClick={handleEdit}>
                {isEditing ? 'Zapisz' : 'Edytuj'}
            </button>
            <button onClick={handleDelete}>Usuń</button>
        </div>
    );
}

export default LocationEntry;
