import { useState, useCallback, useRef, useEffect} from "react";
import {
    APIProvider,
    Map as GoogleMap,
    Marker,
} from '@vis.gl/react-google-maps';

function Map({pins, setPins}) {
    const mapRef = useRef(null);

    const onMapClick = useCallback(async (e) => {
        const latLng = e.detail.latLng;

        const geoJsonData = {
            name: "New Pin",
            loc: {
                type: "Point",
                coordinates: [latLng.lng, latLng.lat], // note the order is reversed
            },
        };

        const res = await fetch(process.env.REACT_APP_SERVER_API_URL + "/pin", {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(geoJsonData),
        });

        if (res.ok) {
            const newPin = await res.json();
            setPins((prevPins) => [...prevPins, newPin]);
        }
    }, [setPins]);


    return (
        <APIProvider apiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
            <GoogleMap
                ref={mapRef}
                style={{width: '100%',height: '500px',}}
                defaultCenter={{lat: 50.06255275511061, lng: 19.939309651193764}}
                defaultZoom={14}
                onClick={onMapClick}
            >
            {pins.map((pin, idx) => (
                <Marker key={idx} position={{
                    lat: pin.loc.coordinates[1], // latitude
                    lng: pin.loc.coordinates[0], // longitude
                }}
                />
            ))}
            </GoogleMap>
        </APIProvider>
    );
}

export default Map;
